<script setup lang="ts">
    const mgrId: Ref<string> = ref('');
    const inviteId: Ref<string> = ref('');
    const startDate: Ref<string> = ref('')
    const frequency: Ref<string> = ref('');
    const membersCount: Ref<string> = ref('');
    const joinDateDeadline: Ref<string> = ref('');
    const contributionAmount: Ref<string> = ref('');

    const { mgrBaseURL } = useRuntimeConfig().public;

    onMounted(()=>{
        const route = useRoute();
        const routeQuery = route.query;

        mgrId.value = routeQuery.mgr_id as string;
        inviteId.value = routeQuery.invite_id as string;
        frequency.value = routeQuery.frequency as string;
        membersCount.value = routeQuery.members as string;
        startDate.value = routeQuery.start_date as string;
        joinDateDeadline.value = routeQuery.join_date_deadline as string;
        contributionAmount.value = routeQuery.contribution_amount as string;
    })

</script>